@mixin breakPoint($breakPoint) {
  @media screen and (min-width: $breakPoint) {
    @content;
  }
}

.with-padding-y {
  padding-top: $spacer;
  padding-bottom: $spacer;
}

.hidden {
  display: none;
}

.hidden-md {
  @include breakPoint($mediumBreakPoint) {
    display: none !important;
  }
}

.hidden-lg {
  @include breakPoint($largeBreakPoint) {
    display: none !important;
  }
}

.visible-md {
  @include breakPoint($mediumBreakPoint) {
    display: block;
  }
}

.visible-lg {
  @include breakPoint($largeBreakPoint) {
    display: block;
