sources.../scss/components/Navbar.scss:
$navbar-height: 56px;

.navbar-spacer {
  height: $navbar-height;
}

.navbar {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: $navbar-height;
  overflow: hidden;
  padding: 0 $spacer;
  display: flex;
  align-items: center;
  box-shadow: 0 3px 5px 1px rgba(0,0,0,.15);
  z-index: 1000;

  a {
    color: inherit;
  }

  .menu-icon {
    font-size: 1.5rem;
    margin: 0 $spacer 0 0;
  }

  .course-title {
    cursor: pointer;
    height: $navbar-height;
    display: flex;
    align-items: center;
    background: rgba(0, 0, 0, .1);
    padding: 0 $spacer / 2;
    margin: 0 $spacer;
    white-space: nowrap;
  }
}

sources.../scss/components/SimplePageLayout.scss:
$sidebar-width: 320px;

.simple-page-layout {
  display: grid;
  height: 100vh;
  grid-template-rows: $navbar-height 1fr;
  grid-template-columns: 1fr;

  >.content {
    overflow-y: auto;
    &:after {
      content: "";
      display: block;
      height: $spacer;
      width: 100%;
    }

    > div {
      margin: 0 auto;
      max-width: $largeBreakPoint; 
    }
  }

  @include breakPoint($largeBreakPoint) {
    grid-template-columns: $sidebar-width 1fr;

    .navbar-spacer {
      grid-column: 1 / 3;
    }
  }
}

sources.../scss/components/PageNavigation.scss:
.page-navigation {
  border-right: 1px solid $light;
  overflow-x: hidden;
  overflow-y: auto;
  height: 100%;
  ul {
    list-style: none;
    padding: 0;
    li {
      cursor: pointer;
      padding: $spacer / 2;
      border-left: 3px solid transparent;
      &.active {
        font-weight: bold;
        border-color: $primary;
        background: $very-light;
      }
    }
  }
}

sources.../scss/components/Box.scss:
.box {
  background: $white;
  border: solid 1px $light;
  border-radius: $baseBorderRadius;
  margin-bottom: $spacer;
  position: relative;
  .title {
    padding: $spacer;
    border-bottom: solid 1px $light;
    text-transform: uppercase;
    margin: 0;
  }
  .content {
    padding: $spacer;
    h5 {
      margin-top: $spacer/2;
    }
  }
  .loading-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(255, 255, 255, .75);
    display: flex;
    align-items: center;
    justify-content: center;
  }
}

sources.../scss/components/Button.scss:
.button {
  display: inline-block;
  color: $white;
  padding: $spacer/3;
  border-radius: $baseBorderRadius;
  font-weight: bold;
  text-shadow: 1px 1px 3px rgba(0, 0, 0, 0.05);
  display: inline-flex;
  font-size:.75rem;
  .ion {
    font-size: 1rem;
  }

  &:not(:last-child) {
    margin-right: $spacer/2;
  }

  @each $name, $color in $main-colors {
    $darker-tone: darken($color: $color, $amount: 10);
    &.button-#{"" + $name} {
      border: $darker-tone solid 1px;
      background-image: linear-gradient($color, $darker-tone);

      &:disabled {
        border: none;
        background: desaturate($color, $amount: 5);
        cursor: not-allowed;
      }

      &:active {
        background: $darker-tone;
      }
    }
  }
}

sources.../scss/components/CodeWidget.scss:
.code-widget {
  @extend .box;
  padding: 0;

  .topbar {
    padding: $spacer/2;
    border-bottom: solid 1px $light;
  }

  .editor {
    min-height: 4rem;
  }

  .output {
    max-height: 190px;
    border-top: 1px solid $light;
    position: relative;
    overflow: hidden;

    i {
      position: absolute;
      top: $spacer;
      right: $spacer;
      font-size: 1.5rem;
      cursor: pointer;
    }

    >.content {
      height: 100%;
      padding: $spacer $spacer*2 $spacer $spacer;
      font-family: monospace;
      font-size: .9rem;
      background: $very-light;
      overflow-y: auto;
      box-sizing: border-box;
    }
  }
}

sources.../scss/components/Spinner.scss:
.inline-spinner {
  width: 50px;
  text-align: center;

  &.white {
    > div {
      background-color: transparentize($color: $white, $amount: .2);
    }
  }

  @-webkit-keyframes sk-bouncedelay {
    0%,
    80%,
    100% {
      -webkit-transform: scale(0);
    }
    40% {
      -webkit-transform: scale(1);
    }
  }

  @keyframes sk-bouncedelay {
    0%,
    80%,
    100% {
      -webkit-transform: scale(0);
      transform: scale(0);
    }
    40% {
      -webkit-transform: scale(1);
      transform: scale(1);
    }
  }

  > div {
    width: 14px;
    height: 14px;
    background-color: $primary;

    border-radius: 100%;
    display: inline-block;
    -webkit-animation: sk-bouncedelay 1.4s infinite ease-in-out both;
    animation: sk-bouncedelay 1.4s infinite ease-in-out both;
  }

  .bounce1 {
    -webkit-animation-delay: -0.32s;
    animation-delay: -0.32s;
  }

  .bounce2 {
    -webkit-animation-delay: -0.16s;
    animation-delay: -0.16s;
  }
}

